css:
.split-view {
  width: 100%;
  height: 100%;
}
.split-view .left {
  float: left;
  width: 100px;
  height: 100%;
}
.split-view .right {
  margin-left: 103px;
  width: 100%;
  height: 100%;
}
.split-view .divider {
  float: left;
  background-color: #181a1f;
  width: 3px;
  height: 100%;
  cursor: col-resize;
}
.process-palette {
  margin: 0;
  padding: 0;
  display: flex;
  flex-direction: column;
  background: #21252b;
}
.process-palette .main-content {
  flex: 1;
  display: flex;
  flex-direction: column;
  overflow: auto;
}
.process-palette .main-content .projects-list {
  /* Ensure that it doesn't overlap with the buttons in the top right. */
  max-width: calc(100% - 20em);
}
.process-palette .main-content .output-view {
  flex: 1;
  display: flex;
  flex-direction: column;
}
.process-palette .button-group {
  display: inline-block;
  position: absolute;
  top: 2px;
  right: 16px;
  z-index: 30;
  text-align: center;
}
.process-palette .help {
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  overflow: auto;
  background: #21252b;
}
.process-palette .help .header {
  margin-top: 6px;
  padding: 0;
}
.process-palette .help .content {
  align-self: center;
  margin-top: 1em;
  margin-bottom: 1em;
  font-size: larger;
  line-height: 175%;
}
.process-list {
  flex: 1;
  display: flex;
  flex-direction: column;
  overflow: auto;
  padding-left: 1em;
  padding-right: 3em;
}
.process-toolbar {
  display: flex;
  flex-direction: row;
  align-items: center;
  margin-bottom: 2px;
}
.process-list-item {
  padding: 2px;
}
.process-list-item table {
  width: 100%;
}
.process-list-item .header {
  color: #6494ed;
  font-size: 115%;
}
.process-list-item .header .keystroke {
  color: #9da5b4;
}
.process-list-item .table-title {
  font-weight: 800;
  padding-right: 24px;
  color: #d7dae0;
  white-space: nowrap;
  width: 7em;
}
.process-list-item .table-button {
  padding-left: 5px;
}
.process-list-item .insert-button {
  padding-top: 1px;
  padding-bottom: 1px;
  margin-left: 0.5em;
}
.process-list-item .command-editor {
  width: 100%;
  box-shadow: none;
  border-radius: 0;
  border-top-style: none;
  border-left-style: none;
  border-right-style: none;
  font-size: inherit;
  border: 1px solid #181a1f;
  background-color: #1b1d23;
}
.output-panel {
  flex: 1;
  overflow: auto;
  white-space: pre;
  background-color: black;
  color: white;
  font-family: Menlo, Consolas, 'DejaVu Sans Mono', monospace;
}
.path-view {
  cursor: pointer;
  text-decoration: underline;
}
.hand-cursor {
  cursor: pointer;
}
.command-edit-view {
  width: 100%;
  height: 100%;
  background-color: #21252b;
}
.command-edit-view .variable-button {
  padding-left: 0px;
}
.command-edit-view .first-column {
  padding-left: 20px;
}
.command-edit-view .top-label {
  vertical-align: top;
}
.command-edit-view .th {
  text-align: center;
}
.command-edit-view .multi-line-editor {
  border: 1px solid #181a1f;
  background-color: #1b1d23;
  min-height: 4.5em;
}
.command-edit-view .check-label {
  padding-left: 10px;
}
.command-edit-view table {
  width: 80%;
  font-size: larger;
}
.command-edit-view td {
  padding-top: 5px;
  padding-left: 10px;
  padding-right: 5px;
}
.pattern-choose-view .pattern-button {
  margin-right: 10px;
}
.pattern-choose-view .pattern-name {
  padding-left: 10px;
}
.pattern-choose-view .pattern-expression {
  padding-left: 10px;
}
.pattern-edit-view .header {
  text-align: center;
}
.table-edit-view {
  border: 1px solid #181a1f;
}
.table-edit-view table {
  width: 100%;
  height: 100%;
  font-size: inherit;
}
.table-edit-view th {
  text-align: center;
}
.table-edit-view td {
  padding-top: 5px;
  padding-left: 3px;
  padding-right: 3px;
  padding-bottom: 0px;
}
.table-edit-view .table-view {
  padding-left: 5px;
  padding-right: 5px;
  padding-bottom: 5px;
}
.table-edit-view .button-view {
  text-align: center;
  padding-top: 5px;
  padding-bottom: 5px;
}
.main-edit-view {
  width: 100%;
  height: 100%;
  background-color: #21252b;
}
.main-edit-view .pointer {
  cursor: pointer;
}
.main-edit-view .left-view {
  width: 100%;
  height: 100%;
  padding: 5px;
}
.main-edit-view .left-view .title {
  vertical-align: middle;
  font-size: larger;
}
.main-edit-view .left-view .reload-button {
  float: right;
}
.main-edit-view .right-view {
  position: relative;
  width: 100%;
  height: 100%;
}
.main-edit-view .add-command-button-view {
  text-align: center;
  padding-top: 5px;
  padding-bottom: 5px;
}
.main-edit-view .edit-patterns-button {
  width: 100%;
}
.main-edit-view .command-choose-view {
  padding: 5px;
}
.main-edit-view .command-choose-view .item-view .name-label {
  width: 100%;
  padding-left: 5px;
  vertical-align: middle;
  display: inline-block;
}
.main-edit-view .command-choose-view .item-view .name-label:hover {
  color: #d7dae0;
}
.main-edit-view .command-choose-view .item-view .highlighted {
  color: #d7dae0;
}
.process-output-view {
  display: flex;
  flex-direction: column;
  flex: 1;
}
.btn-fw {
  min-width: 2em;
}
.project-view {
  margin: 1px;
  padding-left: 0.5em;
  border-style: solid;
  border-width: 1px;
  border-color: #181a1f;
  border-radius: 4px;
  background-color: #282c34;
}
.project-view .project-heading {
  display: flex;
  flex-direction: row;
  align-items: center;
}
.project-view .project-heading .name {
  flex: 1;
  color: #d7dae0;
  font-size: 1.2em;
  padding-left: 3px;
}
.project-view .project-heading .btn {
  margin: 3px;
}
.project-view .project-heading:hover {
  color: #d7dae0;
}
